import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const state = {
  httpError: {
    hasError: false,
    status: '',
    statusText: ''
  }
}

const mutations = {
  ON_HTTP_ERROR (state, playload) {
    state.httpError = playload
  }
}

export default new Vuex.Store({
  state,
  mutations
})
